Attributes.Attribute:
#program;

var slotBuilding = Array::getValueForKey(System::script, 'ObjectInstance');
var animalHabitat = Object::getProperty(slotBuilding, 'MasterBuilding');

// Determine what slot is being modified by checking the index of this building against the animalHabitat's children
var slotIndex = 0;
if(Building::getSubBuilding(animalHabitat, 1) == slotBuilding)
{
    slotIndex = 1;
}
elseif(Building::getSubBuilding(animalHabitat, 2) == slotBuilding)
{
    slotIndex = 2;
}
elseif(Building::getSubBuilding(animalHabitat, 3) == slotBuilding)
{
    slotIndex = 3;
}
elseif(Building::getSubBuilding(animalHabitat, 4) == slotBuilding)
{
    slotIndex = 4;
}

// Now we have slot index and animal choice. Set it on the main building.
var slotStr = 'Slot' + toString(slotIndex);
Object::setVariable(animalHabitat, slotStr, animalChoice, 0, false);

var animalHabitatString = Object::getProperty(animalHabitat, 'Name');
var animalString = 'Empty';
if (animalHabitatString == 'TheEmuExperience')
{
    if (animalChoice == 1)
    {
        animalString = 'Llama';
    }
    elseif (animalChoice == 2)
    {
        animalString = 'Emu';
    }
    elseif (animalChoice == 3)
    {
        animalString = 'Bighorn';
    }
    elseif (animalChoice == 4)
    {
        animalString = 'BuckFawn';
    }
    elseif (animalChoice == 4)
    {
        animalString = 'Armadillo';
    }
}
elseif (animalHabitatString == 'BigCatCountry')
{
    if (animalChoice == 1)
    {
        animalString = 'Lion';
    }
    elseif (animalChoice == 2)
    {
        animalString = 'Cheetah';
    }
    elseif (animalChoice == 3)
    {
        animalString = 'Panther';
    }
    elseif (animalChoice == 4)
    {
        animalString = 'Tiger';
    }
}
elseif (animalHabitatString == 'BaboonCountyUSA')
{
    if (animalChoice == 1)
    {
        animalString = 'Baboon';
    }
    elseif (animalChoice == 2)
    {
        animalString = 'Monkey';
    }
    elseif (animalChoice == 3)
    {
        animalString = 'Lemur';
    }
    elseif (animalChoice == 4)
    {
        animalString = 'Wolverine';
    }
    elseif (animalChoice == 4)
    {
        animalString = 'PingPing';
    }
}
elseif (animalHabitatString == 'GreatPlains')
{
    if (animalChoice == 1)
    {
        animalString = 'Buffalo';
    }
    elseif (animalChoice == 2)
    {
        animalString = 'Zebra';
    }
    elseif (animalChoice == 3)
    {
        animalString = 'Rhino';
    }
    elseif (animalChoice == 4)
    {
        animalString = 'Giraffe';
    }
    elseif (animalChoice == 4)
    {
        animalString = 'Elephant';
    }
}
elseif (animalHabitatString == 'Warmongers')
{
    if (animalChoice == 1)
    {
        animalString = 'Sloth';
    }
    elseif (animalChoice == 2)
    {
        animalString = 'Monkey';
    }
    elseif (animalChoice == 3)
    {
        animalString = 'Rabbit';
    }
    elseif (animalChoice == 4)
    {
        animalString = 'Anteater';
    }
}
Telemetry::logEvent(65003, 'Enumeration', animalString, 'Enumeration', animalHabitatString, 'Enumeration', slotStr);

var pickerBuilding = Building::getSubBuilding(animalHabitat, 0);
GameState_BuildingSelected::setBuilding(pickerBuilding);
System::setGameState('GameState_BuildingSelected');

#program;

var pickerBuilding = Array::getValueForKey(System::script, 'ObjectInstance');
var animalHabitat = Object::getProperty(pickerBuilding, 'MasterBuilding');
var slotBuilding = Building::getSubBuilding(animalHabitat, slotIndex);
GameState_BuildingSelected::setBuilding(slotBuilding);
