Attributes.Attribute:
var textValues = Resource::get('CreditsList.txt');
var baseArray = split(textValues, '\n');
var componentArray = Array::create();
var nameArray = Array::create();

foreach(baseArray as value)
{
	if (strncmp(value,'!', 1) == 0)
	{
		Array::pushBack(componentArray,'TeamTitle');
		Array::pushBack(nameArray, substr(value, 1));
	}
	elseif(strncmp(value,'~', 1) == 0)
	{
		Array::pushBack(componentArray,'FieldTitle');
		Array::pushBack(nameArray, substr(value, 1));
	}
	else
	{
		Array::pushBack(componentArray,'Name');
		Array::pushBack(nameArray, value);
	}
}

var objectID = Menu::eventArray['objectID'];

if (objectID == StringID::fromString('TeamTitleText'))
{
	return 'eFont_Large';
}
elseif (objectID==StringID::fromString('FieldTitleText'))
{
	return 'eFont_NormalBold';
}
elseif (objectID==StringID::fromString('NameText'))
{
	return 'eFont_Small';
}

var menuID   = Menu::getID('eMenu_DynamicScripted');
var objectID = Menu::eventArray['objectID'];
var componentID = Menu::eventArray['componentCount'];

if(objectID==StringID::fromString('TitleText'))
{
	return System::localise('UI_Credits');
}

if (objectID==StringID::fromString('NameText') || objectID==StringID::fromString('FieldTitleText') || objectID == StringID::fromString('TeamTitleText'))
{
	return Array::getValueAtIndex(nameArray, componentID);
}

var menuID   = Menu::getID('eMenu_DynamicScripted');
var objectID = Menu::eventArray['objectID'];

if(objectID==StringID::fromString('ExitButton'))
{
	UISound::close();
	Menu::destroy(menuID);
}
