Attributes.Attribute:
#program;
            var drawLocator = true;

            if( Building::isSelectedInRearrangeMode('Injury500') ||
                Building::isSelectedInRearrangeMode('Injury500StraightShort') ||
                Building::isSelectedInRearrangeMode('Injury500Straight') ||
                Building::isSelectedInRearrangeMode('Injury500CurveSide') )
            {
                var adjacentBuildings = Building::getAdjacentBuildings(buildingInstance);
                foreach(adjacentBuildings as adjacentBuildingInstance)
                {
                    var adjacentLocator = Building::getAdjacentCardinalLocator(adjacentBuildingInstance, buildingInstance, locatorName);
                    if(!isNULL(adjacentLocator))
                    {
                        if(adjacentLocator == 'Injury500_Start' || adjacentLocator == 'Injury500_End')
                        {
                            drawLocator = false;
                            break;
                        }
                    }
                }
            }
            else
            {
                drawLocator = false;
            }

            return drawLocator;

