                    while (User::getVariable('XMAS2016_StoreLevel') < User::getVariable('XMAS2016_MaxStoreLevel') && XMAS2016_CraftingXP >= toDouble(System::getConfigValue('__XMAS2016_GameConfig:Crafting_Targets:CurrentLevel_Target__')))
