Action.Program:
#program;
var burnsInstance = Land::getFirstInstanceOfObject(Character::find('Burns'));

var sleighInstance = Land::getFirstInstanceOfObject(Building::find('WhiteWitchBurnsSleigh'));
var sleighX = toDouble(Building::getProperty(sleighInstance, 'X'));
var sleighZ = toDouble(Building::getProperty(sleighInstance, 'Z'));

Character::snapToPosition(burnsInstance, sleighX, sleighZ);

Character::fadeIn(burnsInstance);

