#program;
var osakaFluLevel = User::getVariable('OsakaFluLevel');
if(osakaFluLevel >= 5)
{
    var maxXP = System::getConfigValue('__AroundTheWorld_BalancingConfig:OsakaFlu:XPForLevel5__');
    var buffer = Array::create('0'=>maxXP,'1'=>' / ','2'=>maxXP);
    return join(buffer);
}
else
{
    var currentXP = User::getCurrencyAmount('AroundTheWorld_OsakaFluXP');
    var targetXP = Selector::getNextValue('AroundTheWorld_OsakaFluXPPerLevel');
    var buffer = Array::create('0'=>toString(currentXP),'1'=>' / ','2'=>toString(targetXP));
    return join(buffer);
}

#program;
var regionCurrency = Land::getCommunityCurrencyAmount('AroundTheWorld_InfectionRegion01');
var regionPopulation = toDouble(System::getConfigValue('__AroundTheWorld_GameConfig:OsakaFlu:PopulationRegion01__'));
var regionPopStep = regionPopulation / 5;
if(regionCurrency < regionPopStep)
{
    return 'ico_aroundtheworld_osakaflumenu_area1_stage1';
}
elseif(regionCurrency < regionPopStep * 2)
{
    return 'ico_aroundtheworld_osakaflumenu_area1_stage2';
}
elseif(regionCurrency < regionPopStep * 3)
{
    return 'ico_aroundtheworld_osakaflumenu_area1_stage3';
}
elseif(regionCurrency < regionPopStep * 4)
{
    return 'ico_aroundtheworld_osakaflumenu_area1_stage4';
}
else
{
    return 'ico_aroundtheworld_osakaflumenu_area1_stage5';
}

