var osakaFluLevel = User::getVariable('OsakaFluLevel');
if(osakaFluLevel >= 5)
{
    return 1;
}
else
{
    var currentXP = User::getCurrencyAmount('AroundTheWorld_OsakaFluXP');
    var targetXP = Selector::getNextValue('AroundTheWorld_OsakaFluXPPerLevel');
    if(targetXP <= 0)
    {
        return 0;
    }
    return currentXP / targetXP;
}
