Action.Program:
#program;

var parrot = Land::getFirstInstanceOfObject(Character::find('WisecrackingParrot'));
var randVal = toDouble(Object::getVariable(parrot, 'rand'));
var newRand = rand(8);
if(randVal!=newRand)
{
Object::setVariable(parrot, 'rand', toString(newRand), 0, false);
}
else
{
Object::setVariable(parrot, 'rand', toString(mod(newRand+1, 8)), 0, false);
}
